// lấy dữ liệu hiển thị
load_data();
function load_data() {
    $.ajax({
        url: "/user/get-info-payment",
        type: "get",
        dataType: "json",
        success: function (data) {
            var html = ``;
            html += `
            <div class="row collections">
                <div class="col-md-1">
                </div>
                <div class="col-md-10">
                    <div class="row">
                        <div class="col-md-6">
                            <div class="row">
                                <div class="col-md-4">
                                    <h4 class="title"><img
                                            src="https://ssg.mediacdn.vn/developer/sg327/1650427859_33065_icon-2-120x120.png"
                                            class="img-full" style="border-radius: 15px 15px 0 0">
                                    </h4>
                                </div>
                                <div class="col-md-8">
                                    <h5>
                                        <img src="data:image/svg+xml;base64,"
                                            class="img-full"> ID của bạn là:
                                        <b>${data.info_user.user_number}</b>
                                    </h5>
                                    <h4><img src="data:image/svg+xml;base64,"
                                            class="img-full"> Số coin: ${data.info_user.coin}</h4>
                                </div>
                            </div>
                        </div>
                        <div class="col-md-3" style="text-align: right">
                            <h4 class="title">
                                <button id="nap-coin" onClick="nap_coin()">Nạp coin</button>
                            </h4>
                            <h4 class="title">
                                <button id="mua-kc" onClick="mua_kc()">Mua KC</button>
                            </h4>
                        </div>
                    </div>

                </div>
                <div class="col-md-1">
                </div>
            </div>

            <div class="row collections" id="payment-content" style="display: none">
                <div class="col-md-1">
                </div>
                <div class="col-md-10">
                    <div class="row">
                        <div class="col-md-5" id="btn-nap-coin" onClick="nap_coin()">Nạp coin</div>
                        <div class="col-md-5" id="btn-mua-kc" onClick="mua_kc()">Mua KC</div>
                    </div>
                    <div class="row" id="payment-content-ct">
                    </div>
                </div>
                <div class="col-md-1">
                </div>
            </div>`;
            $("#payment-account").append(html);
        },
    });
}
// hiện form dành cho nạp coin
function nap_coin() {
    var box = document.querySelector("#payment-content");
    if (box.style.display == "block") {
    } else {
        box.style.display = "block";
    }
    var result = document.getElementById("btn-nap-coin");
    var result1 = document.getElementById("btn-mua-kc");
    result.classList.add("nap-coin-active");
    result1.classList.remove("mua-kc-active");
    $("#payment-content-ct").empty();
    var html = ``;
    html += `

    <div class="col-md-6">
        <div class="row">
            <h4><b>CHỌN SỐ TIỀN CẦN NẠP</b></h4>
            <div class="col-md-12" id="option-coin">
                <button id="btn-payment" value="10">
                    <h5><b>10 COIN</b></h5>
                    <i>Giá: 10 000 VND</i>
                </button>
                <button id="btn-payment" value="50">
                    <h5><b>50 COIN</b></h5>
                    <i>Giá: 50 000 VND</i>
                </button>
                <button id="btn-payment" class="bo" value="100">
                    <h5><b>100 COIN</b></h5>
                    <i>Giá: 100 000 VND</i>
                </button>
                <form>
                    <input name="price" type="tel" placeholder="Nhập giá trị khác ..." pattern="[0-9]*" id="input-payment" value="0" oninput="sum_price()">
                    <div id="sum-price">
                        <h5><b><span id="sum-coin" value="0">0</span> COIN</b></h5>
                        <i>Giá tiền: <span id="sum-price-vnd" value="0">0</span> VND</i>
                    </div>
                </form>
            </div>
        </div>
    </div>
    <div class="col-md-4">
        <div class="row">
            <h4><b>CHỌN LOẠI THẺ</b></h4>
            <div class="col-md-12" id="option-coin">
                <div class="row">
                    <div class="col-md-5" id="atm-payment">
                        <img src="data:image/png;base64," class="img-full" width="50px">
                    </div>
                </div>
                <input type="checkbox"> Tôi đồng ý sử dụng thẻ ATM để thanh toán
            </div>
            <div class="col-md-12">
                <input type="submit" value="XÁC NHẬN" id="submit" onClick="nap()">
            </div>
        </div>
    </div>


    `;
    $("#payment-content-ct").append(html);
    // điền giá trị coin lựa chọn vào ô tổng kết
    $("button").on("click", function () {
        var value = $(this).val();
        $("#input-payment").val(value);
        document.getElementById("sum-coin").innerHTML = value;
        document.getElementById("sum-coin").value = value;
        document.getElementById("sum-price-vnd").innerHTML = value * 1000;
        document.getElementById("sum-price-vnd").value = value * 1000;
    });
}
