// điền dữ liệu vào ô tổng kết nạp coin
function sum_price() {
    var price = $("#input-payment").val();
    document.getElementById("sum-coin").innerHTML = price;
    document.getElementById("sum-coin").value = price;
    document.getElementById("sum-price-vnd").innerHTML = price * 1000;
    document.getElementById("sum-price-vnd").value = price * 1000;
}
// form xác nhận nạp coin
function nap() {
    var sc = document.getElementById("sum-coin");
    var spr = document.getElementById("sum-price-vnd");
    if (sc.value != undefined) {
        $("#id01").empty();
        var html = `
        <div class="w3-modal-content">
            <div class="w3-container">
                <span onclick="document.getElementById('id01').style.display='none'"
                    class="w3-button w3-display-topright">&times;</span>
                <p id="title-submit">XÁC NHẬN NẠP COIN</p>
                <h3>Số coin muốn mua:<span id="numbCoin-submit"><b> ${sc.value} coin</b></span></h3>
                <h3>Tổng tiền:<span id="numbPrice-submit"><b> ${spr.value} VNĐ</b></span></h3>
                <form>
                <p id="info-atm-submit">
                    Tên ngân hàng<br>
                    <input type="text" placeholder="Nhập tên ngân hàng" required id="name-bank"><br>
                    Số tài khoản<br>
                    <input type="text" placeholder="Nhập số tài khoản ngân hàng" required id="stk"><br>
                    Mật khẩu<br>
                    <input type="password" placeholder="Mật khẩu" required id="pw">
                </p>
                <p id="confirm-submit">
                    <input type="submit" value="Xác nhận" onClick="save()">
                </p>
                </form>
            </div>
        </div>`;
        $("#id01").append(html);
        document.getElementById("id01").style.display = "block";
    } else {
        $("#id01").empty();
        var html = `
        <div class="w3-modal-content">
            <div class="w3-container">
                <span onclick="document.getElementById('id01').style.display='none'"
                    class="w3-button w3-display-topright">&times;</span>
                <p id="title-errors">Đã xảy ra lỗi</p>
                <h3>Số coin bạn muốn mua phải lớn hơn 0. Vui lòng nhập lại!</h3>
            </div>
        </div>`;
        $("#id01").append(html);
        document.getElementById("id01").style.display = "block";
    }
}
// lưu trữ lại thông tin nạp coin
function save() {
    var sc = document.getElementById("sum-coin");
    var name_bank = $("#name-bank").val();
    var stk = $("#stk").val();
    var pw = $("#pw").val();
    if (name_bank != "" && stk != "" && pw != "") {
        $.ajax({
            url: "/user/update-payment",
            type: "post",
            dataType: "json",
            data: { coin: sc.value },
            success: function (data) {
                if (data.code === 200) {
                    alert(data.success);
                }
            },
        });
    }
}
function save1(KC) {
    $.ajax({
        url: "/user/update-kc",
        type: "post",
        dataType: "json",
        data: { kc: KC },
        success: function (data) {
            if (data.code === 200) {
                alert(data.success);
            }
        },
    });
}
